import {Component} from '@angular/core';
import {Store} from "@ngrx/store";
import {InfoActions} from "../../store/info.actions";
import {InfoSelectors} from "../../store/info.selectors";

@Component({
  selector: 'app-statistics',
  templateUrl: './statistics.component.html',
  styleUrls: ['./statistics.component.css']
})
export class StatisticsComponent {

  isLoadStatistics$: any;

  constructor(
    private store$: Store
  ) {
    this.isLoadStatistics$ = this.store$.select(InfoSelectors.isLoadStatistics);
  }

  loadStatistics(): void {
    setTimeout(() => {
      this.store$.dispatch(InfoActions.setStatistics({statistics: Math.random()}));
      this.loadStatistics();
    }, 3000);
  }
}
<h3>Component Statistics</h3>
<button type="button" (click)="loadStatistics()">Загружать статистику</button>
